def num_in_handle 
	journaliste = liste
	taille = journaliste.size
	count = 0

	for i in (0..taille)
		 

		j = "#{journaliste[i]}"

		for k in (1..j.size)
			letter = j[k]
			suivant = j[k+1]
			if  letter == "0" || letter == "1"|| letter == "2"|| letter == "3" || letter == "4" || letter == "5" || letter == "6" || letter == "7" || letter == "8" || letter == "9" 
				count += 1 
				if suivant == "0" || suivant == "1" || suivant == "2" || suivant == "3" || suivant == "4" || suivant == "5" || suivant == "6" || suivant == "7" || suivant == "8" || suivant == "9"
				
				 	count -= 1

				 end

			end

		end
	end


	return count 
end



def aude_handle 
	journaliste = liste 
	taille = journaliste.size
	count = 0 


	for i in (0..taille)

		j = "#{journaliste[i]}"
		j.downcase

		for k in (1..j.size)
			if j[k] == "a" && j[k+1] == "u" && j[k+2] == "d" && j[k+3] == "e"
				count += 1 
			end 

		end
	end
	
	return count
end



def first_maj_handle
	journaliste = liste 
	taille = journaliste.size
	count = 0 

	for i in (0..taille)

		j = "#{journaliste[i]}"
		

		if j[1] == "A" ||  j[1] == "B" || j[1] == "C" || j[1] == "D" || j[1] == "E" || j[1] == "F" || j[1] == "G" || j[1] == "H" || j[1] == "I" || j[1] == "J" || j[1] == "K" || j[1]== "M" || j[1] == "N" || j[1] == "O" || j[1] == "P" || j[1] == "Q" || j[1] == "R" || j[1] == "S" || j[1] == "T" || j[1] == "U" || j[1] == "V" || j[1] == "W" || j[1] == "Y" || j[1] == "Z"

			count += 1

		end
	end


	return count

end

def maj_handle
	journaliste = liste 
	taille = journaliste.size
	count = 0 
	for i in (0..taille)

		if (/@["A-Z"]/.match("#{journaliste[i]}"))
			count += 1
		end
	end
	return count 

end

def underscore 
	journaliste = liste
	count = 0
	journaliste.each{ |x| count += x.scan("_").length}

	return count
end

underscore

